mapper.update:
UPDATE
	club c
INNER JOIN 
	professor p
ON 
	c.professor_no = p.professor_no
SET 
	  c.club_name = #{clubName}
	  c.club_content = #{clubContent}
	, c.club_room = #{clubRoom}
	, c.professor_no = #{professorNo}
	, c.professor_name = #{professorName}
	, c.update_date = now()

WHERE c.club_no = #{clubNo}

mapper.insert:
INSERT INTO club
	(
		  club_no
		, club_name
		, club_content
		, club_room
		, professor_no
		, create_date
		, update_date
	)
VALUES
	(
		  #{clubNo}
		, #{clubName}
		, #{clubContent}
		, #{clubRoom}
		, #{professorNo}
		, now()
		, now()
	)
